import { AlertAction, AlertActionTypes } from "./AlertActions";

const initialState = {
  PopUpAlert: false
};

const alertReducer = (state = initialState, action: AlertAction) => {
  switch (action.type) {
    case AlertActionTypes.SET_ALERT:
      return {
        ...state,
        PopUpAlert: true,
      };
    case AlertActionTypes.DISABLE_ALERT:
      return {
        ...state,
        PopUpAlert: false,
      };
    default:
      return state;
  }
};

export default alertReducer;
// End of File (EOF)
