import React, { ChangeEvent } from 'react';
import { Box, Modal, TextField, Button } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles({
  popUp: {
    "&&": {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      backgroundColor: 'white',
      width: '300px',
      p: '1rem',
      borderRadius: '8px',
      padding: 15
    }
  },
  popUpButton: {
    "&&": {
      width: '100%',
      backgroundColor: '#ff5252',
      color: 'white',
      '&:hover': {
        backgroundColor: 'black',
        color: 'white',
      },
    }
  },
})

type Todo = {
  id: string;
  text: string;
  completed?: boolean;
};

type TodoModalProps = {
  selectedTodo: Todo | null;
  handleCloseModal: () => void;
  handleUpdateTodo: (id: string, text: string) => void;
};

const TodoModal = ({
  selectedTodo,
  handleCloseModal,
  handleUpdateTodo,
}: TodoModalProps) => {
  const classes = useStyles();
  const [text, setText] = useState(selectedTodo?.text || '');

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setText(e.target.value);
  };

  const handleUpdate = () => {
    if (selectedTodo) {
      handleUpdateTodo(selectedTodo.id, text.trim());
      handleCloseModal();
    }
  };
  return (
    <Modal open={Boolean(selectedTodo)} onClose={handleCloseModal}>
      <Box className={classes.popUp} >
        <TextField
          label="Edit Todo"
          variant="outlined"
          onChange={handleChange}
          type="text"
          value={text}
          sx={{ width: '100%', mb: '1rem' }}
        />
        <Button
          variant="outlined"
          onClick={updateTodo}
          className={classes.popUpButton}
        >
          Update Todo
        </Button>
      </Box>
    </Modal>
  );
};

export default TodoModal;
