import { hookstate, useHookstate } from "@hookstate/core";

type Todo = {
  id: string,
  text: string,
  completed: false
}

const initialState = hookstate<Todo[]>([]);

export const useGlobalState = () => {
  const state = useHookstate(initialState);

  return state;
};
