import React, { useState } from "react";
import {
  TextField,
  Card,
  CardContent,
  Box,
  Typography,
  Button,
  Stack,
  IconButton, Theme, useMediaQuery
} from "@mui/material";
import { makeStyles, useTheme } from '@mui/styles';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import background from "../assets/backdrop.jpg";

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    backgroundImage: `url(${background})`,
    height: '100vh',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    width: '100vw',
    backgroundRepeat: 'no-repeat',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    backgroundColor: '#ff5252',
    color: 'white',
    padding: '1rem',
    width: '30%',
    height: '70vh',
    boxShadow: '5px 5px 25px -5px rgba(0,0,0,0.5)',
    borderRadius: 0,
    margin: 'auto',
    textAlign: 'left',
    alignItems: 'center',
    justifyContent: 'space-between',
    position: 'relative',
    [theme.breakpoints.down('sm')]: {
      width: '40%',
    },
    [theme.breakpoints.up('lg')]: {
      width: '30%',
    },
  },

}))

const theme = createTheme({
  breakpoints: {
    values: {
      xs: 0,
      sm: 600,
      md: 960,
      lg: 1280,
      xl: 1920,
    },
  },
});

function Todos(): JSX.Element {

  const classes = useStyles();
  //const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <div className={classes.root}>
      <Box
        component="div" className={classes.container}
        sx={{
          backgroundColor: "#ff5252",
          color: "white",
          padding: "1rem",
          width: "30%",
          height: "70vh",
          boxShadow: "5px 5px 25px -5px rgba(0,0,0,0.5)",
          borderRadius: "0px",
          margin: "auto",
          align: "centre",
          textAlign: "left",
          alignItems: "center",
          justifyContent: "space-between",
          position: "relative",
          "@media (min-width: 768px)": {
            width: "40%",
          },
          "@media (min-width: 1592px)": {
            width: "30%",
          },
        }}>
        <Stack direction="column" sx={{ mb: '40px' }}>
          <Typography
            variant="h2"
            sx={{ ml: "50px", mt: "15px", fontWeight: "bold" }}>
            Todo List
          </Typography>
          <Typography
            variant="subtitle1"
            sx={{ marginLeft: "60px", mb: "15px", fontWeight: "bold" }}>
            A simple React Todo List App
          </Typography>
          <hr
            style={{
              margin: "center",
              marginInlineStart: "7%",
              marginInlineEnd: "7%",
            }}
          />
        </Stack>


        <Stack
          direction="column"
          sx={{
            position: "absolute",
            width: "100%",
            left: 0,
          }}>
          <Card
            variant="outlined"
            sx={{
              background: "#ff5252",
              maxHeight: "300px",
              overflowY: "auto",
            }}>
            <CardContent
              sx={{
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
                background: "rgba(255, 205, 210, 0.25)",
              }}>
              <Typography
                variant="h5"
                component="h2"
                color="white"
                marginLeft="50px">
                Task 1
              </Typography>
              <Box marginRight="50px">
                <IconButton aria-label="edit">
                  <EditIcon sx={{ color: "white" }} />
                </IconButton>
                <IconButton aria-label="delele">
                  <DeleteIcon sx={{ color: "white" }} />
                </IconButton>
              </Box>
            </CardContent>
          </Card>
        </Stack>
        <Stack
          direction="column"
          sx={{
            position: "absolute",
            bottom: "50px",
            width: "100%",
            flexDirection: "column",
            marginLeft: "60px",
            marginRight: "50px",
            overflowY: "auto",
          }}>
          <Typography
            variant="h6"
            sx={{ mb: "5px", fontWeight: "bold" }}>
            New Todo
          </Typography>
          <Box sx={{
            gap: "0.4rem", marginRight: "60px", display: "flex"
          }}>
            <TextField
              label={
                <Typography variant="subtitle1" sx={{ fontWeight: "bold" }}>
                  New Todo
                </Typography>
              }
              variant="outlined"
              font-Weight="Bold"
              sx={{
                width: isSmallScreen ? "50%" : "60%",
                fontWeight: "Bold",
                borderRadius: 0,
                "& .MuiOutlinedInput-root": {
                  backgroundColor: "white",
                  "&:hover fieldset": {
                    borderColor: "white",
                  },
                  "&.Mui-focused fieldset": {
                    borderColor: "#ff5252",
                  },
                },
              }}
            />
            <Button
              variant="outlined"
              size="large"
              sx={{
                backgroundColor: "#ff5252",
                borderColor: "white",
                color: "white",
                fontWeight: "bold",
                borderRadius: 0,
                width: isSmallScreen ? "100%" : "auto",
                "&:hover": {
                  backgroundColor: "black",
                },
                "&:focus": {
                  outlineColor: "#ff5252",
                },
              }}>
              Add Todo
            </Button>
          </Box>
        </Stack>
      </Box>
    </div>
  );
}

export default Todos;
// End of File (EOF)
