import { useDispatch, useSelector, connect, ConnectedProps } from "react-redux";
import { Dispatch, bindActionCreators } from "redux"
import { RootState, Todp } from "components/Constants";
import TodoModal from "components/EditTodoModal";
import { editTodo } from "state/todos/TodoActions";
import { setAlert, disableAlert } from "state/todos/AlertActions";

type TodoModalContainerProps = {
  todoId: string | null;
  handleCloseModal: () => void;
}
type StateProps = {
  todos: Todo[];
  alertValue: boolean;
};

type DispatchProps = {
  onEditTodo: (todoId: string, text: string) => void;
  onSetAlert: () => void;
  onDisableAlert: () => void;
};
/*const TodoModalContainer = ({ todoId, handleCloseModal }: TodoModalContainerProps) => {
