  const handleUpdate = (todoId: string, text: string) => {
    dispatch(editTodo(todoId, text));
  }
  const handleSetAlert = () => {
    dispatch(setAlert());
  }

  const handleDisableAlert = () => {
    dispatch(disableAlert());
  }
  return <TodoModal todoId={todoId}
    handleCloseModal={handleCloseModal}
    todos={todos}
    onEditTodo={handleUpdate}
    alertValue={alertValue}
    onSetAlert={handleSetAlert}
    onDisableAlert={handleDisableAlert} />;
};

export default TodoModalContainer;
// End of File (EOF)
*/
const mapStateToProps = (state: RootState): StateProps => {
  return {
    todos: state.todo.todos,
    alertValue: state.alert.PopUpAlert,
  };
};

const mapDispatchToProps = (dispatch: Dispatch): DispatchProps => {
  return bindActionCreators(
    {
      onEditTodo: editTodo,
      onSetAlert: setAlert,
      onDisableAlert: disableAlert,
    },
    dispatch
  );
};

